mapper.select:
select  point_current, point_no
from    t_point
where   user_no = #{user_no}

select place_photo
from t_place
where map_no = #{map_no}

	select a.*
	from t_map a inner join t_like b
	on a.map_no = b.map_no
	where b.user_no = #{user_no}
    and a.theme_no = #{theme_no}
	order by b.like_regdate desc

mapper.update:
UPDATE t_map SET
	   map_like = map_like + 1
	   where map_no = #{map_no}
